export default {
    addSelected (state, ) {
        if (newSelect.length) {
            state.selected.push(newSelect)
            state.newTodo = null
        }
    },
    deleteToDo (state, item) {
        const index = state.toDos.indexOf(item)
        state.toDos.splice(index, 1)
    },
    doneEdit (state) {
        state.editting = null
    }
}
