export default {
    addSelected (state, newSelect) {
        if (newSelect.length && !state.selected.includes(newSelect)) {
            state.selected.push(newSelect)
        }
        else{
            state.selected=state.selected.filter(item => item !== newSelect);
        }
    },
    clearSelected
}
