import axios from 'axios'
function BaseApi() {
    const base="https://localhost:7263/api/v1/"
    function getAll(link){
        axios.get(`${base}${link}`)
        .then((res) => {
            return res
        })
        .catch(res=>{
            return res
        })
    }
}

export default BaseApi
