import axios from 'axios'
const base = "https://localhost:7263/api/v1/"
export function BaseApi() {
    function getAll(link) {
        axios.get(`${base}${link}`)
            .then((res) => {
                return res
            })
            .catch(res => {
                return res
            })
    }
}
